<?php//========================//if(INCLUDED!==true) {	echo "Not Included!"; exit;}//=======================//function saveConfig() {	$cfg = new Config();			// Store New/Changed config items	foreach ($_POST as $item => $val) 	{		$key = explode('__', $item);		if ($key[0] == 'cfg') 		{			$cfg->set($key[1],$val);		}	}	$cfg->Save();	output_message('success','Finished! Config Successfully Updated. Please wait while your redirected... 		<meta http-equiv=refresh content="3;url=index.php?p=admin&sub=siteconfig">');}?>